mapper.select:
select c.registration_id, c.patient_name from patient_case c, inspection i, transaction_log t where i.id = t.collection_id and i.project_id = t.project_id and i.status = 2 and i.case_id = c.registration_id
and c.registration_id = #{caseId}
and c.patient_name = #{patientName}

mapper.update:
update inspection
set status = 4, inspector_role_id = #{inspectorRoleId}
where id = #{collectionId} and project_id = #{projectId}

update inspection
set status = 3
where id = #{collectionId} and project_id = #{projectId}

update inspection
set result_description = #{resultDescription}, result_image = #{resultImage}, advice = #{advice}
where id = #{collectionId} and project_id = #{projectId}
