mapper.select:
select c.registration_id, c.patient_name
from patient_case c, treatment tr, transaction_log t
where tr.id = t.collection_id and tr.project_id = t.project_id
    and tr.case_id = c.registration_id
    and tr.status = 2 and tr.department_id = #{departmentId}

        and c.registration_id = #{caseId}


        and c.patient_name = #{patientName}

mapper.update:
update treatment
set status = 4, treater_role_id = #{treaterRoleId}
where id = #{collectionId} and project_id = #{projectId} and status = 2
